const initialState = 0;

const reducer = (value, action) => {

  if(value === undefined){
    return 0;
  } 

  switch(action.type) {
    case 'INC':
    return value + 1;

    default: 
    return value;
}


}

let state = reducer(initialState, { type: 'INC'});
console.log(state);
state = reducer(state, { type: 'INC'});
console.log(state);





