import React, { useState } from 'react';
import  './header.css';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Avatar from '@material-ui/core/Avatar';

import avatar from '../images/avatar.png';
import logo from '../images/logo.png';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  toolbar: {
    flexGrow: 1,
    display: 'flex',
    flexDirection: 'row-reverse'
  },
}));

const Header = ({balance}) =>{
  const classes = useStyles();

 const [name, updateName] = useState('');   
 const [inputValue, updateInputValue] = useState('');

    const change = (e) => {
        updateInputValue(e.target.value);
    }
    const loginButton = () => {
        if(inputValue.length < 1 || name){
            return updateName('');
        } else {
            return updateName(inputValue);
        }
    };
 // | 'primary'
      // | 'secondary'
      // | 'transparent'
    return(
      <AppBar position="static" color="transparent">       
        
        <Toolbar className={classes.toolbar}>
         <img src={logo} alt={logo} className="logo"/> 
        <div>{balance}$ </div>
          { 
            !name ? 
              <div>
                <TextField id="standard-basic" label="User name" onChange={change} />
                <Button variant="contained" color="primary" variant="contained"  onClick={loginButton}>Log in</Button>
              </div> : 
              <div>
                <Avatar alt="Remy Sharp" src={avatar} />
                <div> {name}</div>
              </div>
          }
        </Toolbar>
      </AppBar>
        // <div className="header_mainDiv">
        //     <div className="mainDiv_leftDiv">
            
        //     <img src={logo} className="logo"alt=""></img>
        //     </div>
        //     <div className="mainDiv_rightDiv">
        //        <div className='left'>
        //        {!name && <input className="mainDiv_input"
        //                  value={inputValue}
        //                  placeholder="Enter your name to login"
        //                  type="text"
        //                  onChange={change}
        //                  ></input> }
        //                  <br></br>
        //           <br></br>
        //           {name &&
        //               <img src={avatar} className="avatar"alt=""></img>}
        //           <br></br>
        //           <br></br>
                 
                         
        //         </div> 
        //        <div className="right">
        //            <p>Balance: <span>{balance}</span></p>
        //            <p className="mainDiv_p">Your name: {name}</p>
        //            <button onClick={loginButton}>{name ? 'Log out' : 'Log In'}</button>
        //         </div>  
        //     </div>
        // </div>
    )
}

export default Header;
