import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Avatar from '@material-ui/core/Avatar';
import CasinoIcon from '@material-ui/icons/Casino';
import { AccessAlarm, ThreeDRotation } from '@material-ui/icons';
import { SvgIcon } from '@material-ui/core';

import avatar from '../images/avatar.png';
import logo from '../images/logo.png';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  toolbarRight: {
    flexGrow: 1,
    display: 'flex',
    flexDirection: ''
  },
  toolbar: {
    flexGrow: 1,
    display: 'flex',
    flexDirection: 'row-reverse',
  },

}));

const Header = ({balance}) =>{
  const classes = useStyles();

 const [name, updateName] = useState('');   
 const [inputValue, updateInputValue] = useState('');

    const change = (e) => {
        updateInputValue(e.target.value);
    }
    const loginButton = () => {
        if(inputValue.length < 1 || name){
            return updateName('');
        } else {
            return updateName(inputValue);
        }
    };
 // | 'primary'
      // | 'secondary'
      // | 'transparent'
    return(
      <AppBar position="static" color="transparent">  
         <div>
            <Toolbar className={classes.toolbar}>
            <div>{balance}$ </div>

              { 
                !name ? 
                  <div>
                    <TextField id="standard-basic" className="standard-basic" label="User name" onChange={change} />
                    
                  </div> : 
                  <div>
                    <Avatar alt="Remy Sharp" src={avatar} />
                    <div> {name}</div>
                  </div>
              }
            </Toolbar>
            <SvgIcon component={CasinoIcon} className="logo" fontSize="large" />
        </div>
      </AppBar>
        // <div className="header_mainDiv">
